import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Checkbox,
  Select,
  MenuItem,
  Box,
  Typography,
  Paper,
} from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import { removeTodo, toggleTodo, updateTodoPriority } from '../features/todosSlice';

const TaskList = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos.todos);

  const handleToggle = (id) => {
    dispatch(toggleTodo(id));
  };

  const handleDelete = (id) => {
    dispatch(removeTodo(id));
  };

  const handlePriorityChange = (id, priority) => {
    dispatch(updateTodoPriority({ id, priority }));
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return '#f44336';
      case 'medium':
        return '#ff9800';
      case 'low':
        return '#4caf50';
      default:
        return '#000';
    }
  };

  return (
    <Paper elevation={2} sx={{ mt: 2, mb: 2 }}>
      <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
        {todos.length === 0 ? (
          <ListItem>
            <ListItemText primary="No tasks yet. Add a new task above!" />
          </ListItem>
        ) : (
          todos.map((todo) => (
            <ListItem
              key={todo.id}
              sx={{
                borderLeft: `4px solid ${getPriorityColor(todo.priority)}`,
                mb: 1,
                '&:hover': {
                  bgcolor: 'action.hover',
                },
              }}
            >
              <Checkbox
                edge="start"
                checked={todo.completed}
                onChange={() => handleToggle(todo.id)}
              />
              <ListItemText
                primary={
                  <Typography
                    sx={{
                      textDecoration: todo.completed ? 'line-through' : 'none',
                    }}
                  >
                    {todo.title}
                  </Typography>
                }
                secondary={new Date(todo.createdAt).toLocaleString()}
              />
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <Select
                  size="small"
                  value={todo.priority}
                  onChange={(e) => handlePriorityChange(todo.id, e.target.value)}
                  sx={{ minWidth: 100 }}
                >
                  <MenuItem value="low">Low</MenuItem>
                  <MenuItem value="medium">Medium</MenuItem>
                  <MenuItem value="high">High</MenuItem>
                </Select>
                <ListItemSecondaryAction>
                  <IconButton
                    edge="end"
                    aria-label="delete"
                    onClick={() => handleDelete(todo.id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </ListItemSecondaryAction>
              </Box>
            </ListItem>
          ))
        )}
      </List>
    </Paper>
  );
};

export default TaskList;
