import React, { useState, useMemo } from 'react';
import {
  Box,
  Typography,
  TextField,
  IconButton,
  Button,
  Select,
  MenuItem,
  InputAdornment,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Checkbox,
  Divider,
} from '@mui/material';
import {
  NotificationsNone as BellIcon,
  Repeat as RepeatIcon,
  CalendarToday as CalendarIcon,
  Star as StarIcon,
  StarBorder as StarBorderIcon,
  ExpandMore as ExpandMoreIcon,
} from '@mui/icons-material';
import { useSelector, useDispatch } from 'react-redux';
import { addTodo, toggleTodo, updateTodoPriority, selectTodo, updateTodoStatus } from '../features/todosSlice';
import { v4 as uuidv4 } from 'uuid';

const VIEW_TITLES = {
  all: 'All Tasks',
  today: 'Today',
  important: 'Important',
  planned: 'Planned',
  assigned: 'Assigned to me',
};

const TaskView = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.auth.user);
  const { todos, selectedTodo, currentView } = useSelector((state) => state.todos);
  const [newTask, setNewTask] = useState('');
  const [status, setStatus] = useState('todo');

  const filteredTodos = useMemo(() => {
    const today = new Date().toDateString();
    let filtered = [...todos];

    // First filter by status
    filtered = filtered.filter(todo => todo.status === status || !todo.status);

    // Then filter by current view
    switch (currentView) {
      case 'today':
        return filtered.filter(todo => 
          new Date(todo.createdAt).toDateString() === today
        );
      case 'important':
        return filtered.filter(todo => todo.priority === 'high');
      case 'planned':
        return filtered.filter(todo => todo.dueDate);
      case 'assigned':
        return filtered.filter(todo => todo.assignedTo === user?.id);
      case 'all':
      default:
        return filtered;
    }
  }, [todos, currentView, status, user?.id]);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim()) {
      const todo = {
        id: uuidv4(),
        title: newTask.trim(),
        completed: false,
        priority: 'medium',
        createdAt: new Date().toISOString(),
        steps: [],
        status: status,
      };
      dispatch(addTodo(todo));
      setNewTask('');
      dispatch(selectTodo(todo.id));
    }
  };

  const handleToggle = (id) => {
    dispatch(toggleTodo(id));
  };

  const handleToggleImportant = (id) => {
    const todo = todos.find(t => t.id === id);
    dispatch(updateTodoPriority({
      id,
      priority: todo.priority === 'high' ? 'medium' : 'high'
    }));
  };

  const handleSelectTodo = (id) => {
    dispatch(selectTodo(id));
  };

  const handleStatusChange = (newStatus) => {
    setStatus(newStatus);
  };

  return (
    <Box sx={{ flex: 1, p: 3, maxWidth: 800 }}>
      <Box sx={{ mb: 4, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h5" fontWeight="600">
          {VIEW_TITLES[currentView]}
        </Typography>
        <Select
          value={status}
          onChange={(e) => handleStatusChange(e.target.value)}
          variant="standard"
          size="small"
          sx={{ minWidth: 120 }}
        >
          <MenuItem value="todo">To Do</MenuItem>
          <MenuItem value="in-progress">In Progress</MenuItem>
          <MenuItem value="done">Done</MenuItem>
        </Select>
      </Box>

      <Box
        component="form"
        onSubmit={handleAddTask}
        sx={{
          mb: 3,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
        }}
      >
        <TextField
          fullWidth
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add A Task"
          variant="standard"
          sx={{
            '& .MuiInput-underline:before': { borderBottom: 'none' },
            '& .MuiInput-underline:hover:before': { borderBottom: 'none' },
          }}
        />
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <IconButton size="small">
            <BellIcon />
          </IconButton>
          <IconButton size="small">
            <RepeatIcon />
          </IconButton>
          <IconButton size="small">
            <CalendarIcon />
          </IconButton>
          <Box sx={{ flex: 1 }} />
          <Button
            variant="contained"
            type="submit"
            sx={{
              bgcolor: 'background.paper',
              color: 'text.primary',
              '&:hover': {
                bgcolor: 'background.default',
              },
            }}
          >
            ADD TASK
          </Button>
        </Box>
      </Box>

      <List>
        {filteredTodos.length === 0 ? (
          <Typography color="text.secondary" align="center" sx={{ py: 4 }}>
            No tasks found
          </Typography>
        ) : (
          filteredTodos.map((todo) => (
            <React.Fragment key={todo.id}>
              <ListItem
                sx={{
                  py: 1,
                  bgcolor: selectedTodo?.id === todo.id ? 'action.selected' : 'transparent',
                  '&:hover': {
                    bgcolor: 'action.hover',
                  },
                  cursor: 'pointer',
                }}
                onClick={() => handleSelectTodo(todo.id)}
              >
                <ListItemIcon>
                  <Checkbox
                    edge="start"
                    checked={todo.completed}
                    onChange={(e) => {
                      e.stopPropagation();
                      handleToggle(todo.id);
                    }}
                  />
                </ListItemIcon>
                <ListItemText
                  primary={
                    <Typography
                      sx={{
                        textDecoration: todo.completed ? 'line-through' : 'none',
                        color: todo.completed ? 'text.secondary' : 'text.primary',
                      }}
                    >
                      {todo.title}
                    </Typography>
                  }
                  secondary={todo.dueDate && (
                    <Typography variant="caption" color="text.secondary">
                      Due {new Date(todo.dueDate).toLocaleDateString()}
                    </Typography>
                  )}
                />
                <IconButton
                  edge="end"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleToggleImportant(todo.id);
                  }}
                >
                  {todo.priority === 'high' ? (
                    <StarIcon color="primary" />
                  ) : (
                    <StarBorderIcon />
                  )}
                </IconButton>
              </ListItem>
              <Divider component="li" />
            </React.Fragment>
          ))
        )}
      </List>
    </Box>
  );
};

export default TaskView;
