import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  IconButton,
  Button,
  Select,
  MenuItem,
  InputAdornment,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Checkbox,
  Divider,
} from '@mui/material';
import {
  NotificationsNone as BellIcon,
  Repeat as RepeatIcon,
  CalendarToday as CalendarIcon,
  Star as StarIcon,
  StarBorder as StarBorderIcon,
  ExpandMore as ExpandMoreIcon,
} from '@mui/icons-material';
import { useSelector, useDispatch } from 'react-redux';
import { addTodo, toggleTodo, updateTodoPriority, selectTodo } from '../features/todosSlice';
import { v4 as uuidv4 } from 'uuid';

const TaskView = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state) => state.todos.todos);
  const selectedTodo = useSelector((state) => state.todos.selectedTodo);
  const [newTask, setNewTask] = useState('');
  const [listType, setListType] = useState('To Do');

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim()) {
      const todo = {
        id: uuidv4(),
        title: newTask.trim(),
        completed: false,
        priority: 'medium',
        createdAt: new Date().toISOString(),
        steps: [],
      };
      dispatch(addTodo(todo));
      setNewTask('');
      dispatch(selectTodo(todo.id));
    }
  };

  const handleToggle = (id) => {
    dispatch(toggleTodo(id));
  };

  const handleToggleImportant = (id) => {
    const todo = todos.find(t => t.id === id);
    dispatch(updateTodoPriority({
      id,
      priority: todo.priority === 'high' ? 'medium' : 'high'
    }));
  };

  const handleSelectTodo = (id) => {
    dispatch(selectTodo(id));
  };

  return (
    <Box sx={{ flex: 1, p: 3, maxWidth: 800 }}>
      <Box sx={{ mb: 4, display: 'flex', alignItems: 'center' }}>
        <Select
          value={listType}
          onChange={(e) => setListType(e.target.value)}
          variant="standard"
          IconComponent={ExpandMoreIcon}
          sx={{ 
            fontSize: '1.5rem',
            fontWeight: 600,
            '&:before': { borderBottom: 'none' },
            '&:after': { borderBottom: 'none' },
          }}
        >
          <MenuItem value="To Do">To Do</MenuItem>
          <MenuItem value="In Progress">In Progress</MenuItem>
          <MenuItem value="Done">Done</MenuItem>
        </Select>
      </Box>

      <Box
        component="form"
        onSubmit={handleAddTask}
        sx={{
          mb: 3,
          display: 'flex',
          flexDirection: 'column',
          gap: 2,
        }}
      >
        <TextField
          fullWidth
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add A Task"
          variant="standard"
          sx={{
            '& .MuiInput-underline:before': { borderBottom: 'none' },
            '& .MuiInput-underline:hover:before': { borderBottom: 'none' },
          }}
        />
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <IconButton size="small">
            <BellIcon />
          </IconButton>
          <IconButton size="small">
            <RepeatIcon />
          </IconButton>
          <IconButton size="small">
            <CalendarIcon />
          </IconButton>
          <Box sx={{ flex: 1 }} />
          <Button
            variant="contained"
            type="submit"
            sx={{
              bgcolor: 'background.paper',
              color: 'text.primary',
              '&:hover': {
                bgcolor: 'background.default',
              },
            }}
          >
            ADD TASK
          </Button>
        </Box>
      </Box>

      <List>
        {todos.map((todo) => (
          <React.Fragment key={todo.id}>
            <ListItem
              sx={{
                py: 1,
                bgcolor: selectedTodo?.id === todo.id ? 'action.selected' : 'transparent',
                '&:hover': {
                  bgcolor: 'action.hover',
                },
                cursor: 'pointer',
              }}
              onClick={() => handleSelectTodo(todo.id)}
            >
              <ListItemIcon>
                <Checkbox
                  edge="start"
                  checked={todo.completed}
                  onChange={(e) => {
                    e.stopPropagation();
                    handleToggle(todo.id);
                  }}
                />
              </ListItemIcon>
              <ListItemText
                primary={
                  <Typography
                    sx={{
                      textDecoration: todo.completed ? 'line-through' : 'none',
                      color: todo.completed ? 'text.secondary' : 'text.primary',
                    }}
                  >
                    {todo.title}
                  </Typography>
                }
              />
              <IconButton
                edge="end"
                onClick={(e) => {
                  e.stopPropagation();
                  handleToggleImportant(todo.id);
                }}
              >
                {todo.priority === 'high' ? (
                  <StarIcon color="primary" />
                ) : (
                  <StarBorderIcon />
                )}
              </IconButton>
            </ListItem>
            <Divider component="li" />
          </React.Fragment>
        ))}
      </List>
    </Box>
  );
};

export default TaskView;
