import React from 'react';
import {
  Box,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Typography,
  Avatar,
  Button,
  IconButton,
} from '@mui/material';
import {
  ViewList as ViewListIcon,
  Today as TodayIcon,
  Star as StarIcon,
  CalendarMonth as CalendarIcon,
  Person as PersonIcon,
  Add as AddIcon,
} from '@mui/icons-material';
import { useSelector } from 'react-redux';
import TaskProgress from './TaskProgress';

const menuItems = [
  { icon: <ViewListIcon />, text: 'All Tasks' },
  { icon: <TodayIcon color="primary" />, text: 'Today', active: true },
  { icon: <StarIcon />, text: 'Important' },
  { icon: <CalendarIcon />, text: 'Planned' },
  { icon: <PersonIcon />, text: 'Assigned to me' },
];

const Sidebar = () => {
  const user = useSelector((state) => state.auth.user);

  return (
    <Box
      sx={{
        width: 280,
        bgcolor: 'background.paper',
        borderRight: '1px solid',
        borderColor: 'divider',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <Box sx={{ p: 3 }}>
        <Box sx={{ mb: 3, display: 'flex', alignItems: 'center', gap: 2 }}>
          <Avatar
            src={user?.avatar}
            alt={user?.username}
            sx={{ width: 48, height: 48 }}
          />
          <Typography variant="h6">
            Hey, {user?.username || 'ABCD'}
          </Typography>
        </Box>

        <List sx={{ width: '100%', mb: 2 }}>
          {menuItems.map((item, index) => (
            <ListItem
              key={index}
              button
              sx={{
                borderRadius: 1,
                mb: 0.5,
                bgcolor: item.active ? 'background.default' : 'transparent',
                '&:hover': {
                  bgcolor: 'action.hover',
                },
              }}
            >
              <ListItemIcon sx={{ minWidth: 40 }}>
                {item.icon}
              </ListItemIcon>
              <ListItemText 
                primary={item.text}
                primaryTypographyProps={{
                  sx: { 
                    fontWeight: item.active ? 600 : 400,
                    color: item.active ? 'primary.main' : 'text.primary',
                  }
                }}
              />
            </ListItem>
          ))}
        </List>

        <Button
          startIcon={<AddIcon />}
          sx={{
            width: '100%',
            justifyContent: 'flex-start',
            color: 'text.secondary',
            '&:hover': { bgcolor: 'action.hover' },
          }}
        >
          Add list
        </Button>
      </Box>

      <Box sx={{ mt: 'auto', p: 3 }}>
        <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 2 }}>
          Today Tasks
        </Typography>
        <TaskProgress />
      </Box>
    </Box>
  );
};

export default Sidebar;
